    axios.get(`http://localhost:5000/recentNews/${id}`).then((res) => {
      setItem(res.data);
      setShowLoader(false);
      console.log(res.data);
    });
  }, [id]);
  return (
    <>
      <Container>
        <Card>
          <Card.Title style={{ margin: "30px", fontSize: "40px" }}>
            {title}
          </Card.Title>
          <Card.Img
            width="400"
            height="385"
            variant="top"
            src="https://akkbd.com/wp-content/uploads/2022/10/Untitled-2.jpg"
          />
          <Card.Body>
            <Card.Text
              style={{
                margin: "20px",
                fontSize: "22px",
                lineHeight: 1.8,
                color: "#666",
              }}
            >
              {des}
            </Card.Text>
          </Card.Body>
        </Card>
      </Container>
    </>
  );
};

export default KhoborDetails;
