import axios from "axios";
import React, { useState } from "react";
import { useForm } from "react-hook-form";
import useAuth from "../../hooks/useAuth";
import "./AddProduct.css";

const AddProduct = () => {
  const [error, setError] = useState(false);
  // const [ratingError,setRatingError] = useState(false);
  const { notify } = useAuth();
  const {
    register,
    handleSubmit,
    reset,
    formState: { errors },
  } = useForm();

  const onSubmit = (data) => {
    setError(false);
    if (
      data.name === "" ||
      data.img === "" ||
      data.description === "" ||
      data.rating === 0
    ) {
      setError(true);
      return;
    }
    axios
      .post("http://localhost:5000/digitalTechnologies/addProduct", data)
      .then((res) => {
        if (res.data.insertedId) {
          notify("success", "Cycle Added Successfully");
        }
      });
    reset();
  };
  return (
    <div>
      <div
        style={{
          width: "100%",
          display: "flex",
          justifyContent: "center",
          alignItems: "center",
          marginTop: "10px",
        }}
      >
        <div className="addCycle-container">
          <div className="form-container">
            <h2
              style={{
                fontWeight: "bold",
                letterSpacing: "1px",
                textAlign: "center",
                margin: "8px 0 32px 0",
                borderBottom: "2px solid  #198754",
                width: "max-content",
              }}
            >
              Add Product
            </h2>
            <form
              noValidate
              autoComplete="off"
              onSubmit={handleSubmit(onSubmit)}
              style={{ display: "felx", flexDirection: "column" }}
            >
              <div style={{ margin: "10px 0" }}>
                <input
                  {...register("name")}
                  // defaultValue={user?.displayName}
                  placeholder="Product Name"
                  className="input-field"
                />
              </div>

              <div style={{ margin: "10px 0" }}>
                <input
                  {...register("img")}
                  type="url"
                  placeholder="Product Image"
                  className="input-field"
                />
              </div>
              <input
                {...register("description")}
                type="text"
                placeholder="Enter Product Description"
                className="input-field"
              />
              <input
                {...register("price")}
                type="text"
                placeholder="Enter  Product Price"
                className="input-field"
              />

              <input type="submit" value="SUBMIT" className="submit-btn" />
              {error && (
                <p style={{ color: "red", letterSpacing: "2px" }}>
                  Input filelds cannot be empty
                </p>
              )}
            </form>
          </div>
        </div>
      </div>
    </div>
  );
};

export default AddProduct;
